package org.EZjava.day04.Control.Loop_exercise;

public class Exercise_Fordouble3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int j = 1; j <=9; j++) {
			for (int i = 2; i<=4; i++) {
				System.out.print(i + "*" + j + "=" + j * i + "\t");
			}
			System.out.println();
		}
	}
}
